func (nd *ndint) Maximum() int {
	idx := nd.NewIndex(0)
	res := nd.Get(idx)

	shape := nd.Shape()
	size := Product(shape)
	for pos := 0; pos < size; pos++ {
		v := nd.Get(idx)
		if v > res {
			res = v
		}
		Increment(idx, shape)
	}
	return res
}

